import {
  isProgramError,
  type Address,
  type SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM,
  type SolanaError,
} from "@solana/kit";
import { SIMPLE_VAULT_PROGRAM_ADDRESS } from "../programs";

/** Overflow: Overflow occurred */
export const SIMPLE_VAULT_ERROR__OVERFLOW = 0x1770; // 6000

export type SimpleVaultError = typeof SIMPLE_VAULT_ERROR__OVERFLOW;

let simpleVaultErrorMessages: Record<SimpleVaultError, string> | undefined;
if (process.env.NODE_ENV !== "production") {
  simpleVaultErrorMessages = {
    [SIMPLE_VAULT_ERROR__OVERFLOW]: `Overflow occurred`,
  };
}

export function getSimpleVaultErrorMessage(code: SimpleVaultError): string {
  if (process.env.NODE_ENV !== "production") {
    return (simpleVaultErrorMessages as Record<SimpleVaultError, string>)[code];
  }

  return "Error message not available in production bundles.";
}

export function isSimpleVaultError<TProgramErrorCode extends SimpleVaultError>(
  error: unknown,
  transactionMessage: {
    instructions: Record<number, { programAddress: Address }>;
  },
  code?: TProgramErrorCode,
): error is SolanaError<typeof SOLANA_ERROR__INSTRUCTION_ERROR__CUSTOM> &
  Readonly<{ context: Readonly<{ code: TProgramErrorCode }> }> {
  return isProgramError<TProgramErrorCode>(
    error,
    transactionMessage,
    SIMPLE_VAULT_PROGRAM_ADDRESS,
    code,
  );
}
